<script  data-wpmeteor-after="REORDER" type="javascript/blocked" data-wpmeteor-type="text/javascript" >
document.querySelector("input#search").addEventListener("keyup", function() {
  let input = document.querySelector("input#search");
  const filter = input.value.toLowerCase();

  // Select all posts
  const posts = document.querySelectorAll("#posts .col-md-6.col-lg-4");

  // Loop on all the posts
  for(post of posts) {
    // Select the title of the post
    let title = post.querySelector('.news_details h4 a');

    // Search title text by filter
    let found = title.innerText.toLowerCase().search(filter);

    // If found === -1 hide the post, otherwise show it
    post.style.display = found === -1 ? "none" : "block";
  }
});
</script>

